function generateUUID(){
  var dt = new Date().getTime();
  var uuid = 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, function(c) {
    var r = (dt + Math.random()*16)%16 | 0;
    dt = Math.floor(dt/16);
    return (c=='x' ? r :(r&0x3|0x8)).toString(16);
  });
  return uuid;
}

const employeeData = [
  {
    "id": generateUUID(),
    "first_name": "Alice",
    "last_name": "Adams",
    "devices": [
      {"id": 1},
      {"id": 2}
    ]
  },
  {
    "id": 2,
    "first_name": "Bob",
    "last_name": "Bush",
    "devices": [
      {"id": 3}
    ]
  },
  {
    "id": 3,
    "first_name": "Chris",
    "last_name": "Carlton",
    "devices": [
      {"id": 4}
    ]
  },
  {
    "id": 4,
    "first_name": "Dorian",
    "last_name": "Descartes",
    "devices": [
      {"id": 5},
      {"id": 6}
    ]
  },
  {
    "id": 5,
    "first_name": "Esteban",
    "last_name": "Enriquez",
    "devices": [
      {"id": 7},
      {"id": 8}
    ]
  }
];

const countEmployees = (data) => {
  let counter = 0;
  data.forEach((employee) => {
    counter++;
  });
  return counter;
};

const countDevices = (data) => {
  let counter = 0;
  data.forEach((employee) => {
    let count = employee.devices.length
    counter += count;
  });
  return counter;
}

export {
  employeeData,
  countEmployees,
  countDevices
};
