    "first_name": "Dorothy",
    "last_name": "Adams",
    "devices": [
      {
        "id": generateUUID(),
        "device_type": "HTC 10"
      },
      {
        "id": generateUUID(),
        "device_type": "Pixel 3 XL"
      }
    ]
  }
];

const countFamily = (data) => {
  let counter = 0;
  data.forEach((employee) => {
    counter++;
  });
  return counter;
};

const countDevices = (data) => {
  let counter = 0;
  data.forEach((employee) => {
    let count = employee.devices.length
    counter += count;
  });
  return counter;
}

export {
  employeeData,
  countFamily,
  countDevices
};
