import React from 'react';
import {
  Button,
  Dialog,
  DialogActions,
  DialogContent,
  DialogContentText,
  DialogTitle,
  Input,
  TextField
} from '@material-ui/core';
import { gql, useMutation } from '@apollo/client';

const ADD_FAMILY_MEMBER = gql`
  mutation AddFamilyMember($first_name: String!, $last_name: String!){
    addFamilyMember(first_name: $first_name, last_name: $last_name){
      id
      first_name
      last_name
    }
  }
`;

export default function FormDialog(props){
  let input;
  const [addFamilyMember, { data }] = useMutation(ADD_FAMILY_MEMBER);

  const handleClose = () => {
    props.toggleFormDialog();
  };

  const submitForm = (event) => {
    event.preventDefault();
    console.log('event', event);
  };

  return(
    <Dialog open={props.formDialogState} onClose={handleClose}>
    <form
        onSubmit={e => {
          e.preventDefault();
          addFamilyMember({ variables: { first_name: input.value, last_name: input.value } });
          input.value = '';
        }}
      >
        <input
          ref={node => {
            input = node;
            console.log('input', input);
            console.log('node', node);
          }}
        />
        <input
          ref={node => {
            input = node;
          }}
        />
        <button type="submit">Add</button>
      </form>

    </Dialog>
  );
};

